
public class ResultantValue {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 2345;
		int y = 8;
		int z = 3;
		int b = 5;
		
		int res = x + y;
		//System.out.println(res);
		
		int f = res / z;
		//System.out.println(f);
		
		int s = f % b;
		//System.out.println(s);
		
		int a = s * b;
		System.out.println("Resultant Value: " + a);
		
		
		
		

	}

}
